import React from "react";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Detail from "./details";
import "./App.css";
import "./main.css";
import axios from "axios";

function App() {
  const [loading, setLoading] = useState(false);
  const [items, setItems] = useState([]);
  const [searchTitle, setSearchTitle] = useState("");
  const navigate = useNavigate();
  useEffect(() => {
    const fecthItem = async () => {
      setLoading(true);
      const results = await axios.get(
        "https://jsonplaceholder.typicode.com/users"
      );
      setItems(results.data);
      setLoading(false);
    };

    fecthItem();
  }, []);
  console.log(searchTitle);
  console.log(
    items.filter((element) =>
      element.name.toLowerCase().includes(searchTitle.toLowerCase())
    )
  );
  return (
    <>
      <BrowserRouter>
        <div className="content">
          <Routes>
            <Route path="/" element={<App />}></Route>
            <Route path="/details" element={<Detail />}></Route>
          </Routes>

          <input
            type="text"
            placeholder="recherche par nom"
            onChange={(e) => setSearchTitle(e.target.value)}
          />

          {loading ? (
            <h4>loading...</h4>
          ) : (
            items
              .filter((element) => {
                if (searchTitle.trim().length <= 3) {
                  return element;
                } else if (
                  element.name
                    .toLowerCase()
                    .includes(searchTitle.trim().toLowerCase())
                ) {
                  return element;
                } else if (searchTitle === "") {
                  return element;
                }
              })
              .map((elt) => (
                <div
                  className="robot-grid"
                  key={elt.id + 100}
                  onClick={() => navigate("/details")}
                >
                  <div className="robot-img">
                    <img
                      src={`https://robohash.org/${elt.id + 100}`}
                      width="150px"
                      alt="robot"
                    ></img>
                    <p>{elt.name}</p>
                    <p>{elt.email}</p>
                  </div>
                </div>
              ))
          )}
        </div>
      </BrowserRouter>
    </>
  );
}

export function Title() {
  return <h1>MES AMIS ROBOT</h1>;
}
export default App;
