import { useEffect, useState } from "react";
import "./App.css";
import "./main.css";
import axios from "axios";

function App() {
  const [loading, setLoading] = useState(false);
  const [items, setItems] = useState([]);
  const [searchTitle, setSearchTitle] = useState("");
  useEffect(() => {
    const fecthItem = async () => {
      setLoading(true);
      const results = await axios.get(
        "https://jsonplaceholder.typicode.com/users"
      );
      setItems(results.data);
      setLoading(false);
    };

    fecthItem();
  }, []);
  console.log(items);
  console.log(items.filter((element) => element.name.includes(searchTitle)));
  // console.log(items.filter((SearchTitle) => items.toLowercase()));
  return (
    <div className="content">
      <input
        type="text"
        placeholder="recherche par nom"
        onChange={(e) => setSearchTitle(e.target.value)}
      />
      {loading ? (
        <h4>loading...</h4>
      ) : (
        items.map((elt) => (
          <div className="robot-grid">
            <div className="robot-img">
              <img
                src={`https://robohash.org/${elt.id}`}
                width="150px"
                alt="robot"
              ></img>
              <p key={elt.id}>{elt.name}</p>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export function Title() {
  return <h1>MES AMIS ROBOT</h1>;
}

export default App;
