import { useEffect, useState } from "react";
import "./App.css";
import "./main.css";
import axios from "axios";

function App() {
  const [item, setItem] = useState([]);
  const [searchTitle, setSearchTitle] = useState([]);
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    const fecthItem = async () => {
      const results = await axios("https://jsonplaceholder.typicode.com/users");
      setItem(results.data);
      setLoading(false);
    };

    fecthItem();
  }, []);

  return (
    <>
      <input
        placeholder="rechercher par nom "
        onChange={(e) => setSearchTitle(e.target.value)}
      />
      {loading ? (
        <h4>loading....</h4>
      ) : (
        item
          .filter((value) => {
            if (searchTitle === "") {
              return value;
            }
          })
          .map((item) => (
            <div key={item.id}>
              <img src={`https://robohash.org/${robot.id}`} alt="robot"></img>
              <p>{robot.name}</p>
            </div>
          ))
      )}

      <div className="robot-grid">
        <div className="robot-img">
          {item.map((robot) => (
            <div key={robot.id}>
              <img src={`https://robohash.org/${robot.id}`} alt="robot"></img>
              <p>{robot.name}</p>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export function Title() {
  return <h1>MES AMIS ROBOT</h1>;
}

export default App;
